  position: relative;
  line-height: 45px;
  padding:0;
  margin:0;
  overflow: hidden;
  border-radius: 3px;
  text-align: auto;
  font-size: 14px;
}
.bn-breaking-news *{
  box-sizing: border-box;
}
.bn-breaking-news.bn-fixed-top{
 /* position: fixed;
